    summ=zeros(1,ncenters); % 统计总数，在这里没用
    [y,lab] = knnfwd(net,testdata); % lab is the class into which the test data is classified
    cla=zeros(1,ncenters);
    cla=[size(lab,1)-size(find(lab-1),1),size(lab,1)-size(find(lab-2),1),...
        size(lab,1)-size(find(lab-3),1),size(lab,1)-size(find(lab-4),1),...
        size(lab,1)-size(find(lab-5),1),size(lab,1)-size(find(lab-6),1),...
        size(lab,1)-size(find(lab-7),1),size(lab,1)-size(find(lab-8),1)];
    clapro=(cla(6)+cla(7)+cla(8))/sum(cla);
    clasum=cla(6)+cla(7)+cla(8);
    if (cla(6)+cla(7)+cla(8))/sum(cla)>=thres1 || (cla(6)+cla(7)+cla(8))>=thres2 % 比例和绝对数值
        [y,ind]=max([0 0 0 0 0 cla(6) cla(7) cla(8)]);
        summ(ind)=summ(ind)+1;
        res=1;
    else
        [y,ind]=max(cla);
        summ(ind)=summ(ind)+1;
        res=0;
    end;
else
